import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import Container from "@material-ui/core/Container";
import Chip from "@material-ui/core/Chip";
export default function Index() {
  var chip = ["Thiết kế website", "Webapp", "Mobile App", "Thiết kế banner"];
  return (
    <Container maxWidth="lg">
      <Grid
        container
        spacing={2}
        justify="center"
        alignItems="center"
        style={{ padding: 13 }}
      >
        <Grid item md={7}>
          <Typography variant="h1">Xin Chào,</Typography>
          <Typography variant="h2">
            Bạn đang cần thiết kế website, phần mềm, hay một thứ gì khác...?
          </Typography>
        </Grid>
        <Grid item md={5}>
          <img style={{ width: "100%" }} src="/img/banner.png" />
        </Grid>
        <Grid item md={5}>
          <img style={{ width: "100%" }} src="/img/careers-mobile.png" />
        </Grid>
        <Grid item md={7}>
          <Typography variant="h2">
            Ước lượng giá nhanh bằng cách chọn thẻ!
          </Typography>
          {chip.map((label) => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
        </Grid>
        <Grid items md={5}>
          <Typography variant="h2">
            Hoặc nhắn tin trực tiếp với chúng tôi!
          </Typography>
        </Grid>
        <Grid item md={7}>
          <img style={{ width: "100%" }} src="/img/choose.jpg" />
        </Grid>
      </Grid>
    </Container>
  );
}
