import HomeIcon from "@material-ui/icons/Home";
import fetch from "isomorphic-fetch";

import { request } from "graphql-request";
import Copyright from "../src/Copyright";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Divider from "@material-ui/core/Divider";
import Container from "@material-ui/core/Container";
import Chip from "@material-ui/core/Chip";
import StackGrid from "react-stack-grid";
import CodeIcon from "@material-ui/icons/Code";
import { useState, useEffect } from "react";
export default function Index({ allDesigns, allSettings, allMaintains }) {
  let [width, setWidth] = useState();
  useEffect(() => {
    window.addEventListener("resize", setWidth(window.innerWidth));
  });
  return (
    <Grid container spacing={0} justify="center" alignItems="center">
      <Grid item xs={12} style={{ backgroundColor: "#212121" }}>
        <Container maxWidth="lg">
          <Grid item xs={12} container justify="center" alignItems="center">
            <Grid item sm={7} md={6}>
              <Typography
                variant="h5"
                align="center"
                style={{
                  marginTop: 21,
                  marginBottom: 21,
                  color: "white",
                  fontWeight: 800
                }}
              >
                Thiết kế website/app phân khúc phổ thông
              </Typography>
              <Typography variant="body1" align="center" color="secondary">
                Giúp bạn sở hữu một website/app đầy sáng tạo với công nghệ mới
                nhất được hàng triệu người sử dụng.
                <br />
                <CodeIcon color="secondary" />
              </Typography>
              <Typography variant="body1" align="center" color="secondary">
                Hân hạnh được hợp tác!
              </Typography>
            </Grid>
            <Grid item sm={5} md={6}>
              <img
                style={{ width: "100%", padding: 21 }}
                src="/img/development.png"
              />
            </Grid>
          </Grid>
        </Container>
      </Grid>
      <Grid item xs={12}>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
          <path
            fill="#212121"
            fillOpacity="1"
            d="M0,32L120,32C240,32,480,32,720,74.7C960,117,1200,203,1320,245.3L1440,288L1440,0L1320,0C1200,0,960,0,720,0C480,0,240,0,120,0L0,0Z"
          ></path>
        </svg>
      </Grid>
      <Grid item xs={12} style={{ marginTop: -75 }}>
        <Container maxWidth="lg">
          <Typography
            variant="body2"
            color="secondary"
            align="center"
            style={{ marginTop: 21 }}
          >
            GIỚI THIỆU
          </Typography>
          <Typography variant="h5" align="center" style={{ fontWeight: 800 }}>
            Nhanh, Rẻ, Đẹp, Hiện Đại.
            <Typography
              variant="body1"
              color="secondary"
              align="center"
              style={{ marginBottom: 21 }}
            >
              Là những gì chúng tôi có và hy vọng mang lại cho bạn nhiều hơn thế
              nữa.
            </Typography>
          </Typography>
          <StackGrid
            columnWidth={
              width >= 1024 ? "25%" : width >= 768 ? "33.33%" : "50%"
            }
          >
            <div key="key1">Item 1</div>
            <div key="key2">Item 2</div>
            <div key="key3">Item 3</div>
          </StackGrid>
          <Typography variant="h4" color="primary">
            Báo giá - 0 đ
          </Typography>
          <Divider
            style={{
              width: "100%",
              height: 1,
              marginTop: 21,
              marginBottom: 21
            }}
          />
          <Typography variant="h5">Chức năng</Typography>
          {allSettings.map(setting => (
            <Chip
              key={setting.id}
              style={{ margin: 5 }}
              label={setting.name}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
          <Divider
            style={{
              width: "100%",
              height: 1,
              marginTop: 21,
              marginBottom: 21
            }}
          />

          <Typography variant="h5">Giao diện</Typography>
          {["Theo yêu cầu", "Làm sẵn", "Siêu đẹp"].map(label => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
          <Divider
            style={{
              width: "100%",
              height: 1,
              marginTop: 21,
              marginBottom: 21
            }}
          />

          <Typography variant="h5">Duy trì</Typography>
          {["Tốc độ trung bình", "Tốc độ cao", "Lượng truy cập lớn"].map(
            label => (
              <Chip
                key={label}
                style={{ margin: 5 }}
                label={label}
                onClick={() => {}}
                color="primary"
                variant="outlined"
                size="small"
              />
            )
          )}
        </Container>
      </Grid>
      <Grid
        item
        xs={12}
        container
        style={{ backgroundColor: "#212121", marginTop: 21, padding: 21 }}
      >
        <Container maxWidth="lg">
          <Grid container spacing={2}>
            <Grid item xs={6} sm={5} md={4} lg={3}>
              <img
                style={{ width: "100%", padding: 21 }}
                src="/img/coder.png"
              />
            </Grid>
            <Grid item xs={6} sm={5} md={4} lg={3}>
              <Typography variant="h6" style={{ color: "white" }}>
                Liên Hệ
              </Typography>
              <Typography variant="body1" color="secondary">
                Trần Ngọc Huy
                <br />
                100 Nguyễn Lương Bằng, Liên Chiểu, Đà Nẵng.
                <br />
                0332813077
                <br />
                truongduchuy910@gmail.com
              </Typography>
              <Typography variant="body1" color="secondary"></Typography>
              <Typography variant="body1" color="secondary"></Typography>
            </Grid>
          </Grid>
          <Copyright />
        </Container>
      </Grid>
    </Grid>
  );
}
Index.getInitialProps = async ctx => {
  var a = "";
  if (process.env.NODE_ENV == "development") a = "http://";
  else a = "https://";
  if (ctx.req) {
    var url = a + ctx.req.headers.host + "/admin/api";
  } else {
    var url = window.location.origin + "/admin/api";
  }
  console.log(url);
  let response = await request(
    url,
    `query {
  allDesigns {
    id
    name
  }
  allSettings {
    id 
    name 
  }
  allMaintains {
    id
    name 
  }
}
`
  );
  console.log(response);
  return response;
};
