import FavoriteIcon from "@material-ui/icons/Favorite";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Divider from "@material-ui/core/Divider";
import Container from "@material-ui/core/Container";
import Chip from "@material-ui/core/Chip";
import StackGrid from "react-stack-grid";

export default function Index() {
  var chip = [
    "Sản phẩm",
    "Tin tức",
    "Mỹ phẩm",
    "Đất",
    "Nhà",
    "Khách sạn",
    "Món ăn",
    "Đặt hàng",
    "Thực phẩm"
  ];
  return (
    <Grid container spacing={0} justify="center" alignItems="center">
      <Grid container style={{ backgroundColor: "#0099ff" }}>
        <Grid item sm={7} md={7}>
          <Typography
            variant="h4"
            color="primary"
            align="center"
            style={{ marginTop: 21, marginBottom: 21 }}
          >
            Thiết kế website/app phân khúc phổ thông!
          </Typography>
          <Typography variant="body1" align="center">
            Giúp bạn sở hữu một website/app có sức sáng tạo về nội dung và giao
            diện. Công cụ quản trị và nền tảng công nghệ mới nhất giúp
            website/app đạt hiệu suất tuyệt vời.
          </Typography>
        </Grid>
        <Grid item sm={5} md={5}>
          <img style={{ width: "100%", padding: 21 }} src="/img/banner.png" />
        </Grid>
      </Grid>{" "}
            <Grid item sm={5}>
        <img style={{ width: "100%" }} src="/img/admin.png" />
      </Grid>
      <Grid item sm={7}>
        <Typography variant="h2">Quản trị đơn giản...!</Typography>
        <Typography variant="body1" style={{ marginTop: 21, marginBottom: 34 }}>
          Chúng tôi xây dựng riêng một trang quản trị để tương thích với các yêu
          cầu của bạn. Bạn sẽ cảm thấy thõa mái với một hệ thống dành riêng cho
          mình.
        </Typography>

        <Typography
          variant="h2"
          color="primary"
          style={{ marginTop: 21, marginBottom: 21 }}
        >
          Giao diện hiện đại!
        </Typography>
        <StackGrid columnWidth="33.33%">
          <div key="key1">Item 1</div>
          <div key="key2">Item 2</div>
          <div key="key3">Item 3</div>
        </StackGrid>
      </Grid>
      <Grid item md={5}>
        <img
          style={{ width: "100%", borderRadius: 13 }}
          src="/img/choose.jpg"
        />{" "}
      </Grid>
      <Grid item md={7}>
        <Typography variant="h2" style={{ marginBottom: 21 }}>
          Báo giá nhanh!
        </Typography>
        <Typography variant="h4" color="primary">
          Khoảng - 0 đ
        </Typography>
        <Divider
          style={{
            width: "100%",
            height: 1,
            marginTop: 21,
            marginBottom: 21
          }}
        />
        <Typography variant="h5">Thiết kế giao diện</Typography>
        {["Theo yêu cầu", "Làm sẵn", "Siêu đẹp"].map(label => (
          <Chip
            key={label}
            style={{ margin: 5 }}
            label={label}
            onClick={() => {}}
            color="primary"
            variant="outlined"
            size="small"
          />
        ))}
        <Divider
          style={{
            width: "100%",
            height: 1,
            marginTop: 21,
            marginBottom: 21
          }}
        />

        <Typography variant="h5">Lắp đặt</Typography>
        {[
          "Sản phẩm",
          "Tin tức",
          "Giỏ hàng",
          "Đặt hàng",
          "Khuyến mãi",
          "Lọc",
          "Tìm kiếm"
        ].map(label => (
          <Chip
            key={label}
            style={{ margin: 5 }}
            label={label}
            onClick={() => {}}
            color="primary"
            variant="outlined"
            size="small"
          />
        ))}
        <Divider
          style={{
            width: "100%",
            height: 1,
            marginTop: 21,
            marginBottom: 21
          }}
        />
        <Typography variant="h5">Duy trì</Typography>
        {["Tốc độ trung bình", "Tốc độ cao", "Lượng truy cập lớn"].map(
          label => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          )
        )}
      </Grid>
      <Grid item md={5}>
        <Typography variant="h2">Hoặc liên hệ nếu có yêu cầu khác!</Typography>
      </Grid>
      <Grid item md={7}>
        <img
          style={{ width: "100%", padding: 21 }}
          src="/img/careers-mobile.png"
        />
      </Grid>
    </Grid>
  );
}
