import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import HomeIcon from "@material-ui/icons/Home";
import fetch from "isomorphic-fetch";

import { request } from "graphql-request";
import Copyright from "../src/Copyright";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Divider from "@material-ui/core/Divider";
import Container from "@material-ui/core/Container";
import Chip from "@material-ui/core/Chip";
import StackGrid from "react-stack-grid";
import CodeIcon from "@material-ui/icons/Code";
import { useState, useEffect } from "react";
export default function Index({
  allDesigns,
  allSettings,
  allMaintains,
  allTemplates
}) {
  let [width, setWidth] = useState();
  useEffect(() => {
    window.addEventListener("resize", setWidth(window.innerWidth));
  });
  return (
    <Grid container spacing={0} justify="center" alignItems="center">
      <Grid item xs={12} style={{ backgroundColor: "#212121" }}>
        <Container maxWidth="lg">
          <Grid item xs={12} container justify="center" alignItems="center">
            <Grid item sm={7} md={6}>
              <Typography
                variant="h5"
                align="center"
                style={{
                  marginTop: 21,
                  marginBottom: 21,
                  color: "white",
                  fontWeight: 800
                }}
              >
                Thiết kế website/app phân khúc phổ thông
              </Typography>
              <Typography variant="body1" align="center" color="secondary">
                Giúp bạn sở hữu một website/app đầy sáng tạo với công nghệ mới
                nhất được hàng triệu người sử dụng.
                <br />
                <CodeIcon color="secondary" />
              </Typography>
              <Typography variant="body1" align="center" color="secondary">
                Hân hạnh được hợp tác!
              </Typography>
            </Grid>
            <Grid item sm={5} md={6}>
              <img
                style={{ width: "100%", padding: 21 }}
                src="/img/development.png"
              />
            </Grid>
          </Grid>
        </Container>
      </Grid>
      <Grid item xs={12}>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
          <path
            fill="#212121"
            fillOpacity="1"
            d="M0,32L120,32C240,32,480,32,720,74.7C960,117,1200,203,1320,245.3L1440,288L1440,0L1320,0C1200,0,960,0,720,0C480,0,240,0,120,0L0,0Z"
          ></path>
        </svg>
      </Grid>
      <Grid item xs={12} style={{ marginTop: width > 768 ? -75 : 0 }}>
        <Container maxWidth="lg">
          <Typography
            variant="body2"
            color="secondary"
            align="center"
            style={{ marginTop: 21 }}
          >
            GIỚI THIỆU
          </Typography>
          <Typography variant="h5" align="center" style={{ fontWeight: 800 }}>
            Nhanh, Rẻ, Đẹp, Hiện Đại.
            <Typography
              variant="body1"
              color="secondary"
              align="center"
              style={{ marginBottom: 21 }}
            >
              Là những gì chúng tôi có và hy vọng mang lại cho bạn nhiều hơn thế
              nữa.
            </Typography>
          </Typography>
          <GridList cols={width >= 1024 ? 6 : width >= 768 ? 4 : 2}>
            {allTemplates.map(template => (
              <GridListTile key={template.id}>
                <img src={template.file.publicUrl} style={{ width: "100%" }} />
              </GridListTile>
            ))}
          </GridList>
          <Typography
            variant="body2"
            color="secondary"
            align="center"
            style={{ marginTop: 21 }}
          >
            BẠN CÓ YÊU CẦU GÌ?
          </Typography>
          <Typography variant="h5" align="center" style={{ fontWeight: 800 }}>
            Nhấn chọn để báo giá!
          </Typography>
          <Typography
            variant="body1"
            color="primary"
            align="center"
            style={{ marginBottom: 21 }}
          >
            0 - đ
          </Typography>

          <Typography variant="h6" style={{ marginTop: 21 }}>
            Chức năng
          </Typography>
          <Typography variant="body1" color="secondary">
            Chọn các chức năng và thành phần bạn muốn xuất hiện trên website/app
            của mình.
          </Typography>

          {allSettings.map(setting => (
            <Chip
              key={setting.id}
              style={{ margin: 5 }}
              label={setting.name}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}

          <Typography variant="h6" style={{ marginTop: 21 }}>
            Giao diện
          </Typography>
          <Typography variant="body1" color="secondary">
            Lựa chọn mức độ, phong cách giao diện.
          </Typography>
          {allDesigns.map(design => (
            <Chip
              key={design.id}
              style={{ margin: 5 }}
              label={design.name}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}

          <Typography variant="h6" style={{ marginTop: 21 }}>
            Duy trì
          </Typography>
          <Typography variant="body1" color="secondary">
            Một website/app có tốc độ tải (load) nhanh giúp nâng cao trải nghiệm
            khách hàng
          </Typography>
          {allMaintains.map(maintain => (
            <Chip
              key={maintain.id}
              style={{ margin: 5 }}
              label={maintain.name}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
        </Container>
      </Grid>

      <Grid item xs={12} container>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 1440 320"
          style={{ position: "absolute", bottom: 0, left: 0, zIndex: -1 }}
        >
          <path
            fill="#212121"
            fillOpacity="1"
            d="M0,32L120,32C240,32,480,32,720,74.7C960,117,1200,203,1320,245.3L1440,288L1440,0L1320,0C1200,0,960,0,720,0C480,0,240,0,120,0L0,0Z"
          ></path>
        </svg>
        <Container maxWidth="lg">
          <Grid container spacing={2}>
            <Grid item xs={6} sm={5} md={4} lg={3}>
              <img
                style={{ width: "100%", padding: 21 }}
                src="/img/coder.png"
              />
            </Grid>
            <Grid item xs={12} sm={5} md={4} lg={3}>
              <Typography variant="h6" style={{ color: "white" }}>
                Liên Hệ
              </Typography>
              <Typography variant="body1" color="secondary">
                Trần Ngọc Huy
                <br />
                100 Nguyễn Lương Bằng, Liên Chiểu, Đà Nẵng.
                <br />
                0332813077
                <br />
                truongduchuy910@gmail.com
              </Typography>
              <Typography variant="body1" color="secondary"></Typography>
              <Typography variant="body1" color="secondary"></Typography>
            </Grid>
          </Grid>
          <Copyright />
        </Container>
      </Grid>
    </Grid>
  );
