import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Divider from "@material-ui/core/Divider";
import Container from "@material-ui/core/Container";
import Chip from "@material-ui/core/Chip";
import StackGrid from "react-stack-grid";

export default function Index() {
  var chip = [
    "Sản phẩm",
    "Tin tức",
    "Mỹ phẩm",
    "Đất",
    "Nhà",
    "Khách sạn",
    "Món ăn",
    "Đặt hàng",
    "Thực phẩm"
  ];
  return (
    <Container maxWidth="lg">
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 1440 320"
        style={{ position: "absolute", zIndex: -1, top: 0, right: 0 }}
      >
        <path
          fill="#F3F4F5"
          fill-opacity="1"
          d="M0,320L40,314.7C80,309,160,299,240,261.3C320,224,400,160,480,160C560,160,640,224,720,218.7C800,213,880,139,960,112C1040,85,1120,107,1200,106.7C1280,107,1360,85,1400,74.7L1440,64L1440,0L1400,0C1360,0,1280,0,1200,0C1120,0,1040,0,960,0C880,0,800,0,720,0C640,0,560,0,480,0C400,0,320,0,240,0C160,0,80,0,40,0L0,0Z"
        ></path>
      </svg>
      <Grid
        container
        spacing={2}
        justify="center"
        alignItems="center"
        style={{ padding: 13 }}
      >
        <Grid item md={7}>
          <Typography variant="h1">Xin Chào,</Typography>
          <Typography variant="h2" color="primary">
            Bạn đang cần thiết kế website/app?
          </Typography>
        </Grid>
        <Grid item md={5}>
          <img style={{ width: "100%", padding: 21 }} src="/img/banner.png" />
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h2" style={{ marginBottom: 21 }}>
            Quản trị đơn giản!
          </Typography>

          <img style={{ width: "100%" }} src="/img/admin.png" />
          <Typography variant="h2" style={{ marginBottom: 21 }}>
            Giao diện hiện đại!
          </Typography>
          <StackGrid columnWidth={150} style={{ justify: "center",alignItems:"center" }}>
            <div key="key1">Item 1</div>
            <div key="key2">Item 2</div>
            <div key="key3">Item 3</div>
          </StackGrid>
        </Grid>
        <Grid item md={5}>
          <img
            style={{ width: "100%", borderRadius: 13 }}
            src="/img/choose.jpg"
          />{" "}
        </Grid>
        <Grid item md={7}>
          <Typography variant="h2" style={{ marginBottom: 21 }}>
            Báo giá nhanh!
          </Typography>
          <Typography variant="h4" color="primary">
            Khoảng - 0 đ
          </Typography>
          <Divider
            style={{
              width: "100%",
              height: 1,
              marginTop: 21,
              marginBottom: 21
            }}
          />
          <Typography variant="h5">Thiết kế giao diện</Typography>
          {["Theo yêu cầu", "Làm sẵn", "Siêu đẹp"].map(label => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
          <Divider
            style={{
              width: "100%",
              height: 1,
              marginTop: 21,
              marginBottom: 21
            }}
          />

          <Typography variant="h5">Lắp đặt</Typography>
          {[
            "Sản phẩm",
            "Tin tức",
            "Giỏ hàng",
            "Đặt hàng",
            "Khuyến mãi",
            "Lọc",
            "Tìm kiếm"
          ].map(label => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
          <Divider
            style={{
              width: "100%",
              height: 1,
              marginTop: 21,
              marginBottom: 21
            }}
          />
          <Typography variant="h5">Duy trì</Typography>
          {["Tốc độ trung bình", "Tốc độ cao", "Lượng truy cập lớn"].map(
            label => (
              <Chip
                key={label}
                style={{ margin: 5 }}
                label={label}
                onClick={() => {}}
                color="primary"
                variant="outlined"
                size="small"
              />
            )
          )}
        </Grid>
        <Grid item md={5}>
          <Typography variant="h2">
            Hoặc liên hệ nếu có yêu cầu khác!
          </Typography>
        </Grid>
        <Grid item md={7}>
          <img
            style={{ width: "100%", padding: 21 }}
            src="/img/careers-mobile.png"
          />
        </Grid>
      </Grid>
    </Container>
  );
}
