import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Divider from "@material-ui/core/Divider";
import Container from "@material-ui/core/Container";
import Chip from "@material-ui/core/Chip";
export default function Index() {
  var chip = [
    "Sản phẩm",
    "Tin tức",
    "Mỹ phẩm",
    "Đất",
    "Nhà",
    "Khách sạn",
    "Món ăn",
    "Đặt hàng",
    "Thực phẩm",
  ];
  return (
    <Container maxWidth="lg">
      <Grid
        container
        spacing={2}
        justify="center"
        alignItems="center"
        style={{ padding: 13 }}
      >
        <Grid item md={7}>
          <Typography variant="h1">Xin Chào,</Typography>
          <Typography variant="h2">Bạn đang cần thiết kế website?</Typography>
        </Grid>
        <Grid item md={5}>
          <img style={{ width: "100%" }} src="/img/banner.png" />
        </Grid>
        <Grid item md={5}>
          <img style={{ width: "100%" }} src="/img/careers-mobile.png" />
        </Grid>
        <Grid item md={7}>
          <Typography variant="h2">Báo giá nhanh!</Typography>
          <Typography variant="h5" color="primary" style={{ marginBottom: 13 }}>
            Thiết kế lắp đặt - 0 đ
          </Typography>
          {chip.map((label) => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
          <Typography variant="h5" color="primary">
            Duy trì hằng năm - 0 đ
          </Typography>
          {chip.map((label) => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
              variant="outlined"
              size="small"
            />
          ))}
        </Grid>
        <Grid item md={5}>
          <Typography variant="h2">
            Hoặc nhắn tin trực tiếp với chúng tôi!
          </Typography>
          <Typography variant="h5">truongduchuy910@gmail.com</Typography>
          <Typography variant="h5">033.281.3077</Typography>
        </Grid>
        <Grid item md={7}>
          <img style={{ width: "100%" }} src="/img/choose.jpg" />
        </Grid>
      </Grid>
    </Container>
  );
}
