import { withAuthSync } from "../utils/auth";
import cookie from "js-cookie";
import { useState } from "react";
import Layout from "../src/Layout";
import AllKetQuas from "../components/ketQuas/allKetQuas";
import CreateKetQua from "../components/ketQuas/createKetQua";
import request from "../components/config/request";
import Logo from "../src/logo";
import Grid from "@material-ui/core/Grid";
import { Typography } from "@material-ui/core";
import LightBoard from "../src/LightBoard";
import ReactMarkdown from "react-markdown";

import Logout from "../components/user/logout";
let Index = props => {
  // variables
  async function getAllCungs() {
    let {
      data: { allKetQuas }
    } = await request({
      body: {
        query: `query {
          allKetQuas(orderBy: "time_DESC") {
            id
            cung {
              name
            }
            ketQua {
              id
              cung {
                name
              }
              chinhTinh {
                id
                name
              }
              phuTinh {
                id
                name
              }
              giai
            }
          }
        }
        `
      }
    });
    return allKetQuas;
  }
  let [allKetQuas, setAllKetQuas] = useState(props.allKetQuas);
  return (
    <Layout>
      <Grid container spacing={1}>
        <Grid item xs={12} md={5}>
          <Logo />
        </Grid>
        <Grid item xs={12} md={7}>
          <LightBoard>
            <img style={{ width: "100%" }} src="/img/tuvibanner.jpg" />
          </LightBoard>
        </Grid>
        <Grid item xs={12}>
          <LightBoard>
            <ReactMarkdown
              source={`Giới Thiệu:

Chào các bạn yêu thích Tử Vi và các mảng khác trong Huyền Học

Mình muốn xây dựng một môi trường chính xác hơn trong việc luận giải tương lai của một con người, và quan trọng nhất là cách CẢI VẬN – GIẢI HẠN hoặc làm giảm đi một phần sự rủi ro trong cuộc sống. Vì thế, mình đã tạo ra hệ thống này, gồm có:

- Hệ thống truy xuất ý nghĩa từng Cung/Chính tinh/Phụ tinh ảnh hưởng trong lá số Tử Vi

- Một số thầy luận giải lá số Tử Vi

- Một số thầy coi chỉ tay- tướng số

- Một số thầy (nếu có thể) ở các mảng khác trong Huyền Học-Bói Toán…

Nội dung bao quát về luận Tử Vi:

I. Luận đoán tướng mạo, tính cách qua lá số:

- Cung mệnh- cung xung chiếu, cung tam hợp, những cung có thể ảnh hưởng

II. Luận đoán anh em, gia đình, bạn bè

- Cung huynh đệ, phụ mẫu, nô bộc, phu thê, tử tức – cung xung chiếu tam hợp

III. Luận đoán vận hạn quá khứ gần nhất có thể của đương số hoặc người trong gia đình

IV. Luận đoán về nghề nghiệp phù hợp, về các mối quan hệ xung quanh

- Luận đoán cụ thể vận hạn năm nay và đại vận chung : Coi đại vận 10 năm, tiểu nguyệt vận hạn

V. Hóa giải hạn trong tương lai, là năm nay hoặc những năm tương lai gần

Nội dung bao quát về xem Chỉ Tay-Tướng số:

- Xem cơ bản là hiện tại và tương lai gần (bao quát hạn hỷ trong năm)

- Hướng giải về cải vận, bẻ tướng, giảm hạn qua việc thay đổi một số thứ trong cuộc sống và tướng mạo

Mình luôn hướng tới việc cải vận, giảm hạn cho mỗi cá nhân, nhưng quan trọng nhất, chúng ta vẫn nên sống Thiện, và ngoài sống Thiện thì “Hãy cho đi, rồi sẽ nhận lại” – “Đức năng, chắc chắn sẽ thắng Số”

Chúc các bạn luôn hạnh phúc. Thân ái!`}
            />
          </LightBoard>
        </Grid>
        <Grid item xs={12}>
          <LightBoard>
            <CreateKetQua
              token={props.token}
              allCungs={props.allCungs}
              allCungVis={props.allCungVis}
              allTinhs={props.allTinhs}
              allPhuTinhs={props.allPhuTinhs}
              onCreate={async ({ data, errors }) => {
                let response = await getAllCungs();
                setAllKetQuas(response);
              }}
            />
          </LightBoard>
        </Grid>

        <Grid item xs={12}>
          <LightBoard>
            <Typography>{props.email}</Typography>
            <Logout />
            <AllKetQuas allKetQuas={allKetQuas} />
          </LightBoard>
        </Grid>
      </Grid>
    </Layout>
  );
};

Index.getInitialProps = async ({ res, client }) => {
  //get variables from server
  const query = `query {
    allCungs {
      id
      name
      tieuDe1
      tieuDe2
      tieuDe3
      tieuDe4
      tieuDe5
    }
    allCungVis {
      id
      name
    }
    allTinhs {
      id
      name
    }
    allPhuTinhs {
      id
      name
    }
    allKetQuas (orderBy: "time_DESC") {
      id
      cung {
        name
      }
      ketQua {
        id
        cung {
          name
        }
        chinhTinh {
          id
          name
        }
        phuTinh {
          id
          name
        }
        giai
      }
    }
  }`;
  try {
    const data = await client.request(query);
    return data;
  } catch (error) {
    cookie.remove("token");
    if (res) {
      res.writeHead(302, { Location: "/login" });
      res.end();
    }
  }
};
export default withAuthSync(Index);
