  return (
    <Container maxWidth="lg">
      <Grid
        container
        spacing={2}
        justify="center"
        alignItems="center"
        style={{ padding: 13 }}
      >
        <Grid item md={7}>
          <Typography variant="h1">Xin Chào,</Typography>
          <Typography variant="h2" color="primary">
            Bạn đang cần thiết kế website?
