          {[
            "Sản phẩm",
            "Tin tức",
            "Giỏ hàng",
            "Đặt hàng",
            "Khuyến mãi",
            "Lọc",
            "Tìm kiếm"
          ].map(label => (
            <Chip
              key={label}
              style={{ margin: 5 }}
              label={label}
              onClick={() => {}}
              color="primary"
