          />
          <Typography variant="h5">Duy trì</Typography>
          {["Tốc độ trung bình", "Tốc độ cao", "Lượng truy cập lớn"].map(
            label => (
              <Chip
                key={label}
                style={{ margin: 5 }}
                label={label}
                onClick={() => {}}
                color="primary"
                variant="outlined"
                size="small"
              />
            )
          )}
        </Grid>
        <Grid item md={5}>
          <Typography variant="h2">
            Hoặc liên hệ nếu có yêu cầu khác!
          </Typography>
          <Typography variant="h5" color="primary">truongduchuy910@gmail.com</Typography>
          <Typography variant="h5" color="primary">033.281.3077</Typography>
        </Grid>
        <Grid item md={7}>
          <img
            style={{ width: "100%", padding: 21 }}
            src="/img/careers-mobile.png"
          />
        </Grid>
      </Grid>
    </Container>
  );
}
