Index.getInitialProps = async ctx => {
  var a = "";
  if (process.env.NODE_ENV == "development") a = "http://";
  else a = "https://";
  if (ctx.req) {
    var url = a + ctx.req.headers.host + "/admin/api";
  } else {
    var url = window.location.origin + "/admin/api";
  }
  let response = await request(
    url,
    `query {
  allDesigns {
    id
    name
  }
  allSettings {
    id 
    name 
  }
  allMaintains {
    id
    name 
  }
  allTemplates {
    id
    file {
      publicUrl
    }
  }
}
`
  );
  response.url = url;
  return response;
};
